import React ,{useState} from 'react';


export default function AjouterLivre(){

    const [Titere,setTitere] =useState('');
    const [Nombre,setNombre] = useState('');
    const [Auteur,setAuteur] = useState('');
    const [Imge,setImge] = useState('');

function Ajout(e){
    e.preventDefalt();

}

    return(
        <form onSubmit={Ajout} >
            <fieldset>
                <legend style={{textAlign:"center",background}}>
                    <u><h2>Ajouter un Livre</h2></u>
                </legend>
                <div >
                    <label>Titere   :   </label>
                    <input type="text" onChange={(e)=>setTitere(e.targit.value)}/>
                </div>
                <div>
                    <label>Nombre Pages   :   </label>
                    <input type="text" onChange={(e)=>setNombre(e.targit.value)}/>
                </div>
                <div>
                    <label>Auteur   :   </label>
                    <input type="text" onChange={(e)=>setAuteur(e.targit.value)}/>
                </div>
                <div>
                    <label>Imge   :   </label>
                    <input type="file" onChange={(e)=>setImge(e.targit.value)}/>
                </div>
                <br/><br/><br/><br/>
                <button type='submit'>Ajouter Livre</button>

            </fieldset>
        </form>
    )
}
