    useEffect(
        axios.get(' https://www.robotstxt.org/robotstxt.html')
        .then((A) => setcours(A.data))
        , []);

    return (
        <>
            <input type='text' placeholder='Rechercher' onChange={(e) => setinp(e.target.value)}/>

            {cours.filter((c) => c.inp === inp)
                .map((c,i)=>(
            <div key={i}>
                <p>{c.inp}</p>
                <img src={c.img} alt={c.inp} />
                <p>{c.hhhh}</p>
            </div>

            ))


}
        </>
    )
}
