import React, { useState } from 'react';

export default function Afficher() {
    // Liste originale des livres (constante)
    const originalList = [
        { Titre: "Robotique", nbpages: "196", Auteur: "SABBAI", photo: "photo1.jpg" },
        { Titre: "Réseaux", nbpages: "59", Auteur: "Tanger", photo: "photo2.jpg" },
        { Titre: "Dév Informatique", nbpages: "88", Auteur: "MEKOUAR", photo: "photo3.jpg" },
        { Titre: "Informatique", nbpages: "125", Auteur: "HASSOUNI", photo: "photo4.jpg" }
    ];

    // State contenant la liste affichée
    const [t, setT] = useState(originalList);

    // Fonction pour supprimer un élément sans modifier l'ordre des autres
    function supprimer(A) {
        setT(t.filter((i) => i.Titre !== A.Titre)); // Supprime en comparant le titre
    }

    // Fonction pour réinitialiser la liste dans son ordre original
    function resetListe() {
        setT(originalList);
    }

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Liste des Livres</h1>
            {t.map((list, i) => (
                <div key={i} style={{ border: "1px solid black", padding: "10px", margin: "10px" }}>
                    <img src={list.photo} alt={list.Titre} style={{ width: "50px", height: "50px" }} />
                    <p><strong>Titre:</strong> {list.Titre}</p>
                    <p><strong>Auteur:</strong> {list.Auteur}</p>
                    <p><strong>Nb Pages:</strong> {list.nbpages}</p>
                    <button onClick={() => supprimer(list)}>Supprimer</button>
                </div>
            ))}
            <button 
                onClick={resetListe} 
                style={{ display: "block", margin: "20px auto", padding: "10px" }}>
                Réinitialiser la liste
            </button>
        </div>
    );
}
